import React, { useContext, useState } from 'react'
import {useDropzone} from 'react-dropzone'
import Head from 'next/head'

import { Container, Title, Description, Input } from '../styles/pages/Home'
import { Context } from '../context/Context'

const Home: React.FC = () => {
  const { 
    canvasRef, 
    lowerText, 
    setLowerText, 
    setUpperText, 
    upperText 
  } = useContext(Context)
  const {getRootProps, getInputProps} = useDropzone({})
  const [name] = useState({
    title: 'Meme Generator',
    description: 'Meme Generator or editing.'
  })
  return (
    <Container>
      <Head>
        <title>Meme Generator</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>
      <Title>{name.title}</Title>
      <Description>{name.description}</Description>

      <main>
        <canvas 
        {...getRootProps} 
        ref={canvasRef} 
        style={{background: 'red'}}
        onClick={() => {
          alert(canvasRef.current);
        }}
        >
            <input {...getInputProps} />
        </canvas>
        <form>
          <div>
            <label htmlFor="upperText">Title</label>
            <Input 
              value={upperText}
              onChange={(e) => setUpperText(e.target.value)}
            />
          </div>
          <div>
          <label htmlFor="lowerText">Description</label>
            <Input 
              value={lowerText}
              onChange={(e) => setLowerText(e.target.value)}
            />
          </div>
        </form>
      </main>

    </Container>
  )
}

export default Home
