import React, { useContext, useState } from 'react'
import { useDropzone } from 'react-dropzone'
import Head from 'next/head'

import {
  Container,
  Title,
  Description,
  Input,
  Label,
  DivInput
} from '../styles/pages/Home'
import { Context } from '../context/Context'

const Home: React.FC = () => {
  const {
    canvasRef,
    lowerText,
    setLowerText,
    setUpperText,
    upperText
  } = useContext(Context)
  const { getRootProps, getInputProps } = useDropzone({})
  const [name] = useState({
    title: 'Meme Generator',
    description: 'Meme Generator or editing.'
  })
  return (
    <Container>
      <Head>
        <title>Meme Generator</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>
      <Title>{name.title}</Title>
      <Description>{name.description}</Description>

      <main>
        <canvas
          {...getRootProps}
          ref={canvasRef}
          style={{ background: 'red', margin: 10, width: 400, borderRadius: 10 }}
          onClick={() => {
            alert(canvasRef.current);
          }}
        >
          <input {...getInputProps} />
        </canvas>
        <form>
          <DivInput>
            <Label htmlFor="upperText">Title</Label>
            <Input
              value={upperText}
              onChange={(e) => setUpperText(e.target.value)}
            />
          </DivInput>
          <DivInput>
            <Label htmlFor="lowerText">Description</Label>
            <Input
              value={lowerText}
              onChange={(e) => setLowerText(e.target.value)}
            />
          </DivInput>
        </form>
      </main>

    </Container>
  )
}

export default Home
